import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { SharedService } from '../shared.service';

@Component({
  selector: 'app-first',
  templateUrl: 'first.component.html',
  styleUrls: ['first.component.css'],
})
export class FirstComponent implements OnInit{
  //Zad1-4
  title: string = "Pierwsza wartość przekazana z komponentu";
  imieINazwisko: string = "";
  isLargeFont: boolean = false;
  isGreenText: boolean = false;
  constructor(private router: Router, private sharedService: SharedService) { }

ngOnInit() : void{}

PokazImie(){
  this.imieINazwisko = "Kamil Beza";
}

Rozmiar() {
  this.isLargeFont = !this.isLargeFont;
}

Kolor() {
  this.isGreenText = !this.isGreenText;
}

Lacznie(){
  this.isLargeFont = !this.isLargeFont;
  this.isGreenText = !this.isGreenText;
}

//Zad5
// ulubioneLinki = [
//   { nazwa: 'Google', adres: 'https://www.google.com', kolor: 'black' },
//   { nazwa: 'GitHub', adres: 'https://github.com', kolor: 'black' },
//   { nazwa: 'OpenAI', adres: 'https://www.openai.com', kolor: 'black' },
//   { nazwa: 'YouTube', adres: 'https://www.youtube.com', kolor: 'black' }
// ];

// zmienKolor(link: any) {
//   link.kolor = link.kolor === 'black' ? 'blue' : 'black';
// }

//Zad6
ulubioneLinki = [
  { nazwa: 'Google', adres: 'https://www.google.com', wybrany: false },
  { nazwa: 'GitHub', adres: 'https://github.com', wybrany: false },
  { nazwa: 'OpenAI', adres: 'https://www.openai.com', wybrany: false },
  { nazwa: 'YouTube', adres: 'https://www.youtube.com', wybrany: false },
  { nazwa: 'X', adres: 'https://twitter.com/home?lang=pl', wybrany: false }
];

wybranyLinkIndex: number | null = null;

zmienWybor(link: any, index: number) {
  this.wybranyLinkIndex = index;
  // Wyzeruj wybór dla pozostałych linków
  this.ulubioneLinki.forEach((l, i) => {
    if (i !== index) {
      l.wybrany = false;
    }
  });
}

//Zad7
// counterValue: number = 0;

// zwieksz() {
//   this.counterValue++;
// }

// zmniejsz() {
//     this.counterValue--;
// }

//Zad8
  counterValue: number = 0;

  zmienWartosc(liczba: number) {
    this.counterValue += liczba;
  }

//Zad9
zdjecia: string[] = [
  'https://picsum.photos/200/300',
  'https://picsum.photos/200/350',
  'https://picsum.photos/200/400',
  'https://picsum.photos/200/500',
];

aktualneZdjecie: string = this.zdjecia[0];
info: string = '';

poprzednieZdjecie() {
  const obecnyIndex = this.zdjecia.indexOf(this.aktualneZdjecie);
  if (obecnyIndex > 0)
  {
    this.aktualneZdjecie = this.zdjecia[obecnyIndex - 1];
    this.info = '';
  }else
  this.info = 'To jest pierwsze zdjęcie';
}

nastepneZdjecie() {
const obecnyIndex = this.zdjecia.indexOf(this.aktualneZdjecie);
if(obecnyIndex < this.zdjecia.length - 1)
{
  this.aktualneZdjecie = this.zdjecia[obecnyIndex + 1];
  this.info = '';
}else {
  this.info = 'To jest ostatnie zdjęcie';
}
}

czyPierwszeZdjecie(): boolean {
  return this.zdjecia.indexOf(this.aktualneZdjecie) === 0;
}

czyOstatnieZdjecie(): boolean {
  return this.zdjecia.indexOf(this.aktualneZdjecie) === this.zdjecia.length - 1;
}

//Zad12
//Wyświetl komponent o nazwie second
navigateToSecondComponent() {
  this.router.navigate(['second']);
}

//Zad13
get isSecondComponentVisible(): boolean {
  return this.sharedService.getIsSecondComponentVisible();
}

method() {
  return "Wartość zwrócona z metody"
}
}
<h1>{{title}}</h1>
<p>{{method()}}</p>

<button (click)="PokazImie()">Click</button>
<button (click)="Rozmiar()">Rozmiar</button>
<button (click)="Kolor()">Kolor</button>
<button (click)="Lacznie()">Dziala Lacznie</button>
<p [class.large-font]="isLargeFont" [class.green-text]="isGreenText">{{ imieINazwisko }}</p>

<!--Zad5-->
<!-- <h2>Ulubione linki:</h2>
<ul>
  <li *ngFor="let link of ulubioneLinki">
    <a [style.color]="link.kolor" (click)="zmienKolor(link)">{{ link.nazwa }}
        (<a [href]="link.adres" target="_blank">{{ link.adres }}</a>)
    </a>
  </li>
</ul> -->

<!--Zad6-->
<h2>Ulubione linki:</h2>
<ul>
    <li *ngFor="let link of ulubioneLinki; let i = index">
      <a [class.selected]="i === wybranyLinkIndex" (click)="zmienWybor(link, i)">
        {{ link.nazwa }} (<a [href]="link.adres" target="_blank">{{ link.adres }}</a>)
      </a>
    </li>
  </ul>

<!--Zad7-->
<p>{{ counterValue }}</p>
<button (click)="zmienWartosc(1)">+1</button>
<button (click)="zmienWartosc(-1)">-1</button>
<button (click)="zmienWartosc(2)">+2</button>
<button (click)="zmienWartosc(-2)">-2</button>

<!--Zad9,10,11 disabled- znika jeśli spełni się warunek czyPierwszeZdjecie-->
<h2>Galeria Zdjęć</h2>
<!-- <button (click)="poprzednieZdjecie()">Poprzednie</button>
<button (click)="nastepneZdjecie()">Następne</button> -->
<button (click)="poprzednieZdjecie()" disabled="czyPierwszeZdjecie()">Poprzednie</button><!--Dzięki *ngIf znika guzik aby naprawić: Disabled="bez !- negacji"-->
<button (click)="nastepneZdjecie()" *ngIf="!czyOstatnieZdjecie()">Następne</button>
<div class="info" *ngIf="info">{{ info }}</div> <br><br>
<img [src]="aktualneZdjecie" alt="Zdjęcie" *ngIf="aktualneZdjecie">

<!--Zad12 Zrób jeszcze bez routera-->
<br>
<button (click)="navigateToSecondComponent()">Przejdź do drugiego komponentu</button>
<router-outlet *ngIf="isSecondComponentVisible"></router-outlet>
