import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-firstc',
  templateUrl: './firstc.component.html',
  styleUrls: ['./firstc.component.css']
})
export class FirstcComponent {

  secondComponent:boolean=false;

  Pokaz(){
    this.secondComponent = !this.secondComponent;
  }

  Ukryj(){
    this.secondComponent = false;
  }

  message:string="";
  Tak(){
    this.secondComponent = false;
    this.message = "Super";
  }

  Nie(){
    this.message = "Nie super";
    this.secondComponent = false;
  }
}
<button (click)="Pokaz()">Kliknij mnie</button>
<div *ngIf="secondComponent">
    <app-secondc (schowaj)="Ukryj()" (tak)="Tak()" (nie)="Nie()"></app-secondc>
    <p>{{message}}</p>
</div>
