//   { nazwa: 'Google', adres: 'https://www.google.com', kolor: 'black' },
//   { nazwa: 'GitHub', adres: 'https://github.com', kolor: 'black' },
//   { nazwa: 'OpenAI', adres: 'https://www.openai.com', kolor: 'black' },
//   { nazwa: 'YouTube', adres: 'https://www.youtube.com', kolor: 'black' }
// ];

// zmienKolor(link: any) {
//   link.kolor = link.kolor === 'black' ? 'blue' : 'black';
// }

ulubioneLinki = [
  { nazwa: 'Google', adres: 'https://www.google.com', wybrany: false },
  { nazwa: 'GitHub', adres: 'https://github.com', wybrany: false },
  { nazwa: 'OpenAI', adres: 'https://www.openai.com', wybrany: false },
  { nazwa: 'YouTube', adres: 'https://www.youtube.com', wybrany: false }
];

wybranyLinkIndex: number | null = null;

zmienWybor(link: any, index: number) {
  this.wybranyLinkIndex = index;
  // Wyzeruj wybór dla pozostałych linków
  this.ulubioneLinki.forEach((l, i) => {
    if (i !== index) {
      l.wybrany = false;
    }
  });
}

//Zad7
counterValue: number = 0;

zwiększ() {
  this.counterValue++;
}

zmniejsz() {
  if (this.counterValue > 0) {
    this.counterValue--;
  }
}

method() {
  return "Wartość zwrócona z metody"
}
}
