import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-first',
  templateUrl: 'first.component.html',
  styleUrls: ['first.component.css'],
})
export class FirstComponent implements OnInit{
  //Zad1-4
  title: string = "Pierwsza wartość przekazana z komponentu";
  imieINazwisko: string = "";
  isLargeFont: boolean = false;
  isGreenText: boolean = false;
constructor(){}
ngOnInit() : void{}

PokazImie(){
  this.imieINazwisko = "Kamil Beza";
}

Rozmiar() {
  this.isLargeFont = !this.isLargeFont;
}

Kolor() {
  this.isGreenText = !this.isGreenText;
}

Lacznie(){
  this.isLargeFont = !this.isLargeFont;
  this.isGreenText = !this.isGreenText;
}

//Zad5
ulubioneLinki = [
  { nazwa: 'Google', adres: 'https://www.google.com', kolor: 'black' },
  { nazwa: 'GitHub', adres: 'https://github.com', kolor: 'black' },
  { nazwa: 'OpenAI', adres: 'https://www.openai.com', kolor: 'black' },
  { nazwa: 'YouTube', adres: 'https://www.youtube.com', kolor: 'black' }
];

zmienKolor(link: any) {
  link.kolor = link.kolor === 'black' ? 'red' : 'black';
}

method() {
  return "Wartość zwrócona z metody"
}
}
