import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class SharedService {
  private isSecondComponentVisible = true;

  getIsSecondComponentVisible(): boolean {
    return this.isSecondComponentVisible;
  }

  hideSecondComponent() {
    this.isSecondComponentVisible = false;
  }
}
