import { style } from '@angular/animations';
import { Component } from '@angular/core';
import { Link } from '../Models/link';
import { BehaviorSubject, Observable } from 'rxjs';

@Component({
  selector: 'app-first',
  templateUrl: './first.component.html',
  styleUrls: ['./first.component.css']
})
export class FirstComponent {
  par:string = "";
  wiekszy:boolean = false;
  kolor:boolean = false;

  Pokaz(){
    this.par="Luki";
  }

  Powieksz(){
    this.wiekszy = !this.wiekszy;
  }

  Kolor(){
    this.kolor = !this.kolor;
  }

  Razem(){
    this.wiekszy = !this.wiekszy;
    this.kolor = !this.kolor;
  }

  linksTable : Link[] = [
    new Link("Google","www.google.pl", "white"),
    new Link("FB", "www.fb.com", "white"),
    new Link("NK", "NK.pl", "white")
  ]

  links = new BehaviorSubject<Link[]>(this.linksTable);

  get links$():Observable<Link[]>{
    return this.links.asObservable();
  }

  // ZmienKolor(link: Link, nowyKolor: string) {
  //   link.kolor = nowyKolor;
  // }

  //Zad6
  zaznaczonyLink: Link | null = null;
  ZmienKolor(link: Link, nowyKolor: string) {
    // Zmien kolor tylko dla aktualnie zaznaczonego linku
    if (this.zaznaczonyLink === link) {
      link.kolor = nowyKolor;
    } else {
      // Jeśli kliknięto na inny link, odznacz poprzedni
      if (this.zaznaczonyLink) {
        this.zaznaczonyLink.kolor = 'white'; // Ustaw kolor na domyślny (lub inny kolor)
      }

      // Zaznacz nowy link
      this.zaznaczonyLink = link;
      link.kolor = nowyKolor;
    }
  }
}
